export class ArticlesService {
	static articlesUrl = `${process.env.REACT_APP_API_URL}/articles`;

	static getArticles() {
		return fetch(this.articlesUrl)
			.then(res => res.json())
			.catch(err => {
				console.log(err);
			})
	}

	static getArticle(id: string) {
		return fetch(`${this.articlesUrl}/${id}`)
			.then(res => res.json())
			.catch(err => {
				console.log(err);
			});
	}
}
